// src/api/authApi.js
import axiosClient from "./axiosClient";


const googleLogin = (token) => axiosClient.post("http://localhost:3010/auth/google", { token });


const authApi = {
  login: (email, password) =>
    axiosClient.post("/auth/login", { email, password }),

  register: (fullName, email, password) =>
    axiosClient.post("/auth/register", {
      full_name: fullName, 
      email,
      password,
    }),
  changePassword: (payload) =>
    axiosClient.put("/auth/change-password", payload),

  forgotPassword: (email) =>
    axiosClient.post("/auth/forgot-password", { email }),

  resetPassword: (token, newPassword) =>
    axiosClient.post("/auth/reset-password", { token, newPassword }),

  // For Google login when backend is ready
  loginWithGoogle: (token) => googleLogin(token),
};

export default authApi;
