// src/pages/GoogleTest.jsx
import React from "react";
import { useGoogleLogin, GoogleOAuthProvider } from "@react-oauth/google";

const GOOGLE_CLIENT_ID = "YOUR_GOOGLE_CLIENT_ID_HERE"; // Replace this

export default function GoogleTest() {
  const login = useGoogleLogin({
    onSuccess: tokenResponse => {
      console.log("Google token:", tokenResponse);
      alert("Login successful! Check console for token.");
    },
    onError: () => {
      console.error("Google login failed");
      alert("Google login failed. Check console.");
    },
  });

  return (
    <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
      <div style={{ padding: 50 }}>
        <h2>Google OAuth Test</h2>
        <button onClick={login} className="btn btn-outline-danger">
          Login with Google
        </button>
      </div>
    </GoogleOAuthProvider>
  );
}
